import { useState, useEffect } from "react";
import MovieList from "./MovieList";
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';

function Home() {

    const [movieArrr, setMovieArrr] = useState([]);

    const sendReq = async () => {
        const serverURL = `http://localhost:3005/trend`;
        const response = await fetch(serverURL);
        const data = await response.json();

        setMovieArrr(data);
        console.log(data)
    }

    useEffect(()=>{
        sendReq();
    }, [])
    return (
        <>
            <h1>Home</h1>
            <MovieList/>

            <Row xs={1} md={4} className="g-4">
                {memesArrr.map((item) => {
                    return <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSqw8vmlYi7VaU8peOBxAWE0FmM_d7roPLpWk0t7q1k3g&s" />
                            <Card.Body>
                                <Card.Title>{item.title}</Card.Title>
                                <Card.Text>
                                    <p>{item.overview}</p>

                                </Card.Text>
                                <Button variant="primary">Go somewhere</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                })}
            </Row>
        </>
    );
}

export default Home;
