function Home() {

    const [memesArr, setMemesArr] = useState([]);

    const sendReq = async () => {
        const serverURL = `http://localhost:3005/trend`;
        const response = await fetch(serverURL);
        const data = await response.json();

        setMemesArr(data);
        console.log(data)
    }

    useEffect(()=>{
        sendReq();
    }, [])
    return (
        <>
            <h1>Home</h1>
            {/* <button onClick={sendReq}>send req</button> */}
            <Row xs={1} md={4} className="g-4">
                {memesArr.map((item) => {
                    return <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src="https:/${item.poster_path}" />
                            <Card.Body>
                                <Card.Title>{item.title}</Card.Title>
                                <Card.Text>
                                    <p>{item.overview}</p>

                                </Card.Text>
                                <Button variant="primary">Go somewhere</Button>
                            </Card.Body>
                        </Card>
                    </Col>
                })}
            </Row>
        </>
    );
}

export default Home;
